</span></span><span class="lake-preview-line" style="background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);"><span class="lake-preview-codeblock-content" style="background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);">    <span class="cm-keyword" style="color: rgb(215, 58, 73); background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);">public</span> <span class="cm-variable" style="background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);">User</span> <span class="cm-variable" style="background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);">create</span>(<span class="cm-meta" style="color: rgb(31, 127, 154); background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);">@RequestBody</span> <span class="cm-variable" style="background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);">User</span> <span class="cm-variable" style="background: none 0% 0% / auto repeat scroll padding-box border-box rgba(0, 0, 0, 0);">user</span>) {
